    let intro = useRef(null);
    const [music, setMusic] = useState([]);
    useEffect(() => {
        // textIntro(intro);
        const getData = () => {
            fetch("data.json", {
                    headers: {
                      "Content-Type": "application/json",
                      Accept: "application/json"
                    }
            })
            .then((response) => response.json())
            .then((data) => setMusic(data));
            gsap.from('.intro', {
                xPercent: -20,
                opacity: 0,
                stagger: 0.2,
                duration: 2,
